DMutation::DMutation(char refAA, int pos, char curAA) : refAA(refAA), pos(pos), curAA(curAA) {}

JSON::Json DRM::ToJson() const
{
    JSON::Json root;
    root["name"] = name;
    root["positions"] = PositionStrings();
    return root;
}

JSON::Json ExpectedMinor::ToJson() const
{
    JSON::Json root;
    root["position"] = position;
    root["aminoacid"] = aminoacid;
    root["codon"] = codon;
    return root;
}

TargetGene::TargetGene(const int begin, const int end, const std::string& name,
                       const std::vector<DRM>& drms, const std::vector<ExpectedMinor>& minors)
    : begin(begin), end(end), name(name), drms(drms), minors(minors)
{
}

JSON::Json TargetGene::ToJson() const
{
    JSON::Json root;
    root["begin"] = begin;
    root["end"] = end;
    root["name"] = name;
    std::vector<JSON::Json> drmJson;
    for (const auto& d : drms)
        drmJson.emplace_back(d.ToJson());
    root["drms"] = drmJson;
    std::vector<JSON::Json> minorJson;
    for (const auto& m : minors)
        minorJson.emplace_back(m.ToJson());
    root["expectedminors"] = minorJson;
    return root;
}

JSON::Json TargetGene::ToJson(const std::vector<TargetGene>& genes)
{
    JSON::Json root;
    std::vector<JSON::Json> genesJson;
    for (const auto& g : genes)
        genesJson.emplace_back(g.ToJson());
    root["genes"] = genesJson;
    return root;
}

TargetConfig::TargetConfig(const std::string& input)
{
    const auto inputString = DetermineConfigInput(input);
    if (!inputString.empty()) {
        const auto inputJson = JSON::Json::parse(inputString);
        this->targetGenes = TargetConfig::TargetGenesFromJson(inputJson);
        this->referenceSequence = TargetConfig::RootTagFromJson(inputJson, "referenceSequence");
        this->referenceName = TargetConfig::RootTagFromJson(inputJson, "referenceName");
        this->version = TargetConfig::RootTagFromJson(inputJson, "version");
    }
}

std::string TargetConfig::DetermineConfigInput(std::string input)
{
    if (input.size() == 0) return "";

    // Pre-process TC tags
    if (input == "HIV_HXB2") input = "HIV";

    // Target assignment
    std::string output;
    if (input.at(0) == '{') {
        output = input;
    } else if (Utility::FileExists(input)) {
        std::ifstream t(input);
        output = std::string((std::istreambuf_iterator<char>(t)), std::istreambuf_iterator<char>());
    } else if (input.at(0) == '<' && input.at(input.size() - 1) == '>' &&
               predefinedConfigs_.find(input.substr(1, input.size() - 2)) !=
                   predefinedConfigs_.end()) {
        output = predefinedConfigs_.at(input.substr(1, input.size() - 2));
    } else if (predefinedConfigs_.find(input) != predefinedConfigs_.end()) {
        output = predefinedConfigs_.at(input);
    } else {
        std::cerr << "Warning: Target config -c is not a file, JSON string, or tag: " << input
                  << std::endl;
    }

    if (output.empty())
        std::cerr << "Warning: Empty target config -c, specified region will be "
                     "treated as coding region"
                  << std::endl;
    return output;
}

bool TargetConfig::HasExpectedMinors() const
{
    for (const auto& g : targetGenes)
        if (!g.minors.empty()) return true;
    return false;
}

size_t TargetConfig::NumExpectedMinors() const
{
    size_t numExpectedMinors = 0;
    for (const auto& g : targetGenes)
        numExpectedMinors += g.minors.size();
    return numExpectedMinors;
}

std::string TargetConfig::RootTagFromJson(const JSON::Json& root, const std::string& tag)
{
    if (root.empty() || root.find(tag) == root.cend() || root[tag].empty())
        return "";
    else
        return root[tag];
}

std::vector<TargetGene> TargetConfig::TargetGenesFromJson(const JSON::Json& root)
{
    std::vector<TargetGene> genes;
    if (root.empty() || root.find("genes") == root.cend() || root["genes"].empty()) return genes;
    for (const auto& jGene : root["genes"]) {
        TargetGene g;
        if (jGene.find("name") == jGene.cend()) throw std::runtime_error("Missing name in gene.");
        g.name = jGene["name"];
        if (jGene.find("begin") == jGene.cend()) throw std::runtime_error("Missing begin in gene.");
        g.begin = jGene["begin"];
        if (jGene.find("end") == jGene.cend()) throw std::runtime_error("Missing end in gene.");
        g.end = jGene["end"];
        std::vector<DRM> drms;
        if (jGene.find("drms") != jGene.cend()) {
            for (const auto& jDrm : jGene["drms"]) {
                DRM drm;
                if (jDrm.find("name") == jDrm.cend())
                    throw std::runtime_error("Missing name in drm in gene " + g.name);
                drm.name = jDrm["name"];
                if (jDrm.find("positions") == jDrm.cend())
                    throw std::runtime_error("Missing positions in drm in gene " + g.name);
                if (jDrm["positions"].empty())
                    throw std::runtime_error("Empty positions in drm in gene " + g.name);
                for (const std::string& p : jDrm["positions"]) {
                    std::regex rgx("([a-zA-Z]*)([0-9]+)([a-zA-Z]*)");
                    std::smatch match;

                    if (std::regex_search(p.begin(), p.end(), match, rgx)) {
                        std::string refAAs = match[1];
                        if (refAAs.empty()) refAAs = "*";
                        int pos = std::stoi(match[2]);
                        std::string curAAs = match[3];
                        if (curAAs.empty()) curAAs = "*";

                        for (const auto& a : refAAs)
                            for (const auto& b : curAAs)
                                drm.positions.emplace_back(a, pos, b);
                    }
                }
                drms.emplace_back(std::move(drm));
            }
        }
        g.drms = std::move(drms);
        std::vector<ExpectedMinor> minors;
        if (jGene.find("expectedminors") != jGene.cend()) {
            for (const auto& jMinor : jGene["expectedminors"]) {
                ExpectedMinor minor;
                if (jMinor.find("position") == jMinor.cend())
                    throw std::runtime_error("Missing position in expectedminors in gene " +
                                             g.name);
                minor.position = jMinor["position"];
                if (jMinor.find("aminoacid") == jMinor.cend())
                    throw std::runtime_error("Missing aminoacid in expectedminors in gene " +
                                             g.name);
                minor.aminoacid = jMinor["aminoacid"];
                if (jMinor.find("codon") == jMinor.cend())
                    throw std::runtime_error("Missing codon in expectedminors in gene " + g.name);
                minor.codon = jMinor["codon"];
                minors.emplace_back(std::move(minor));
            }
        }
        g.minors = std::move(minors);
        genes.emplace_back(std::move(g));
    }
    return genes;
}

